    $sql = "select * from usuarios where usuario = :usu";

    $sentencia = $conexion -&gt; prepare($sql);
    $sentencia -&gt; bindParam (':usu', $usu);  
    $sentencia -&gt; execute();

    $usuario = $sentencia -&gt; fetch();

    if($usuario &amp;&amp; password_verify($_POST['password'], $usuario['password'])) {
        echo "OK!";
    } else {
